#import <objc/runtime.h>

#import "UIWindow+BFKit.h"
#import "UIDevice+BFKit.h"
#import "UIImage+BFKit.h"


@implementation UIWindow (BFKit)

BOOL sendEventExchanged = NO;
NSMutableDictionary *touchImages;

- (UIImage * _Nonnull)takeScreenshotAndSave:(BOOL)save {
    BOOL ignoreOrientation = SYSTEM_VERSION_GREATER_THAN_OR_EQUAL_TO(@"8.0");

  UIInterfaceOrientation orientation = UIInterfaceOrientationPortrait;
  Class UIApplicationClass = NSClassFromString(@"UIApplication");
  if(UIApplicationClass && [UIApplicationClass respondsToSelector:@selector(sharedApplication)]) {
    orientation = [[[UIApplication class] performSelector:@selector(sharedApplication)] statusBarOrientation];
  }
    CGSize imageSize = CGSizeZero;
    if (UIInterfaceOrientationIsPortrait(orientation) || ignoreOrientation) {
        imageSize = [UIScreen mainScreen].bounds.size;
    } else {
        imageSize = CGSizeMake([UIScreen mainScreen].bounds.size.height, [UIScreen mainScreen].bounds.size.width);
    }
    
    UIGraphicsBeginImageContextWithOptions(imageSize, NO, [UIScreen mainScreen].scale);
    CGContextRef context = UIGraphicsGetCurrentContext();
    CGContextSaveGState(context);
    CGContextTranslateCTM(context, self.center.x, self.center.y);
    CGContextConcatCTM(context, self.transform);
    CGContextTranslateCTM(context, -self.bounds.size.width * self.layer.anchorPoint.x, -self.bounds.size.height * self.layer.anchorPoint.y);
    
    if (!ignoreOrientation) {
        if (orientation == UIInterfaceOrientationLandscapeLeft) {
            CGContextRotateCTM(context, (CGFloat)M_PI_2);
            CGContextTranslateCTM(context, 0, -imageSize.width);
        } else if (orientation == UIInterfaceOrientationLandscapeRight) {
            CGContextRotateCTM(context, (CGFloat)-M_PI_2);
            CGContextTranslateCTM(context, -imageSize.height, 0);
        } else if (orientation == UIInterfaceOrientationPortraitUpsideDown) {
            CGContextRotateCTM(context, (CGFloat)M_PI);
            CGContextTranslateCTM(context, -imageSize.width, -imageSize.height);
        }
    }
    
    if ([self respondsToSelector:@selector(drawViewHierarchyInRect:afterScreenUpdates:)]) {
        [self drawViewHierarchyInRect:self.bounds afterScreenUpdates:NO];
    } else {
        [self.layer renderInContext:UIGraphicsGetCurrentContext()];
    }
    
    CGContextRestoreGState(context);
    UIImage *image = UIGraphicsGetImageFromCurrentImageContext();
    UIGraphicsEndImageContext();
    
    if (save)
        UIImageWriteToSavedPhotosAlbum(image, nil, nil, nil);
    
    return image;
}

- (UIImage * _Nonnull)takeScreenshot {
    return [self takeScreenshotAndSave:NO];
}

- (void)takeScreenshotWithDelay:(CGFloat)delay save:(BOOL)save completion:(void (^ _Nullable)(UIImage *image))completion {
    dispatch_after(dispatch_time(DISPATCH_TIME_NOW, delay * NSEC_PER_SEC), dispatch_get_main_queue(), ^{
        completion([self takeScreenshotAndSave:save]);
    });
}

- (void)activateTouch {
    if (sendEventExchanged) {
        return;
    } else {
        touchImages = [[NSMutableDictionary alloc] init];
    }
    
    Method sendEvent = class_getInstanceMethod([self class], @selector(sendEvent:));
    Method exchangedSendEvent = class_getInstanceMethod([self class], @selector(exchangedSendEvent:));
    method_exchangeImplementations(sendEvent, exchangedSendEvent);
    
    sendEventExchanged = YES;
}

- (void)deactivateTouch {
    if (!sendEventExchanged) {
        return;
    }
    
    Method sendEvent = class_getInstanceMethod([self class], @selector(sendEvent:));
    Method exchangedSendEvent = class_getInstanceMethod([self class], @selector(exchangedSendEvent:));
    method_exchangeImplementations(exchangedSendEvent, sendEvent);
    
    sendEventExchanged = NO;
}

- (void)exchangedSendEvent:(UIEvent *)event {
    [self handleEvent:event];
    [self exchangedSendEvent:event];
}

- (void)handleEvent:(UIEvent * _Nonnull)event {
    if (event.type != UIEventTypeTouches)
        return;
    
    for (UITouch *touch in event.allTouches) {
        UITouchPhase phase = touch.phase;
        
        switch (phase) {
            case UITouchPhaseBegan: {
                UIImageView *touchImageView = [[UIImageView alloc] initWithImage:self.touchImage];
                touchImageView.center = [touch locationInView:self];
                [self addSubview:touchImageView];
                
                [touchImages setObject:touchImageView forKey:[NSString stringWithFormat:@"%li", (long)touch.hash]];
                
                touchImageView.alpha = 0.0;
                touchImageView.transform = CGAffineTransformMakeScale(1.2, 1.2);
                
                [UIView animateWithDuration:0.2 animations:^{
                    touchImageView.alpha = 1.0;
                    touchImageView.transform = CGAffineTransformMakeScale(1, 1);
                }];
                break;
            }
            case UITouchPhaseMoved:
            case UITouchPhaseStationary: {
                UIImageView *touchImageView = touchImages[[NSString stringWithFormat:@"%li", (long)touch.hash]];
                
                touchImageView.center = [touch locationInView:self];
                break;
            }
            case UITouchPhaseEnded:
            case UITouchPhaseCancelled: {
                UIImageView *touchImageView = touchImages[[NSString stringWithFormat:@"%li", (long)touch.hash]];
                
                [UIView animateWithDuration:0.2 animations:^{
                    touchImageView.alpha = 0.0;
                    touchImageView.transform = CGAffineTransformMakeScale(1.2, 1.2);
                } completion:^(BOOL finished) {
                    [touchImageView removeFromSuperview];
                    touchImageView.alpha = 1.0;
                    [touchImages removeObjectForKey:[NSString stringWithFormat:@"%li", (long)touch.hash]];
                }];
                break;
            }
        }
    }
}

- (UIImage * _Nonnull)touchImage {
    UIImage *image = [UIImage imageWithData:[[NSData alloc] initWithBase64EncodedString:@"" options:NSDataBase64DecodingIgnoreUnknownCharacters]];
    
    if ([[UIScreen mainScreen] scale] == 2) {
        return [image imageByScalingProportionallyToSize:CGSizeMake((image.size.width / 5) * 3, (image.size.width / 5) * 3)];
    } else {
        return [image imageByScalingProportionallyToSize:CGSizeMake((image.size.width / 3) * 2, (image.size.width / 3) * 2)];
    }
}

@end
